components/products/price:
{{#if price.with_tax}}
    <div class="price-section price-section--withTax" {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
        {{#if price.rrp_with_tax}}
            <span data-product-rrp-with-tax class="price price-regular">{{price.rrp_with_tax.formatted}}</span>
        {{/if}}
        <span data-product-price-with-tax class="price">{{price.with_tax.formatted}}</span>
        {{#if price.without_tax}}
            {{lang 'products.price_with_tax' tax_label=price.tax_label}}
        {{/if}}
    </div>
{{/if}}

{{#if price.without_tax}}
    <div class="price-section price-section--withoutTax {{#if price.with_tax}}price-section--minor{{/if}}"  {{#if schema_org}}itemprop="offers" itemscope itemtype="https://schema.org/Offer"{{/if}}>
        {{#if price.rrp_without_tax}}
            <span data-product-rrp-without-tax class="price price-regular" style="color: #716d6d;">{{price.rrp_without_tax.formatted}}</span>
        {{/if}}
        {{#if schema_org}}
            <meta itemprop="price" content="{{price.without_tax.value}}">
            <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
        {{/if}}
        <span data-product-price-without-tax class="price price--withoutTax"{{#if price.rrp_without_tax}} style="color: #ef0000;"{{/if}}>
        	{{price.without_tax.formatted}}
        </span>
        {{#if price.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
        {{/if}}
    </div>
{{/if}}

{{#if page_type '===' 'product'}}
    {{#if price.saved}}
        <div class="price-section price-section--saving">
            <span class="price-you-save">
                {{lang 'products.you_save' amount=price.saved.formatted}}
            </span>
        </div>
    {{/if}}
{{/if}}
